definition.script:
pipeline {
    agent any

    stages {
        stage('Build Catalog tar.gz') {
            steps {
                sh "export CATALOG_NAME=${env.FILENAME}; create-catalog.sh;"     
            }
        }
        stage('Post build action'){
            steps {
                sh "export  POST_CATALOG_CREATION_SCRIPT='${env.POST_CATALOG_CREATION_SCRIPT}'; eval \"\$POST_CATALOG_CREATION_SCRIPT\""     

            }
        }
    }
}
